from flask import Flask, render_template, request, redirect, url_forfrom flask_wtf import FlaskForm, CSRFProtectfrom sqlalchemy.exc import SQLAlchemyErrorfrom wtforms import StringField, SubmitFieldfrom wtforms.validators import DataRequiredfrom flask_sqlalchemy import SQLAlchemyfrom sqlalchemy.orm import DeclarativeBase, Mapped, mapped_columnfrom sqlalchemy import Integer, String, Float, descapp = Flask(__name__)# app.secret_key = 'Hitesh123'# csrf = CSRFProtect(app)class Base(DeclarativeBase):    passapp.config['SQLALCHEMY_DATABASE_URI'] = 'sqlite:///bookstore-collection.db'db = SQLAlchemy(model_class=Base)db.init_app(app)all_books = []class Book_Store(db.Model):    id: Mapped[int] = mapped_column(Integer, primary_key=True)    title: Mapped[str] = mapped_column(String(250), unique=True, nullable=False)    author: Mapped[str] = mapped_column(String(250), nullable=False)    rating: Mapped[str] = mapped_column(Float, nullable=False)    def __repr__(self):        return f'<Book {self.title}>'with app.app_context():    db.create_all()class BookForm(FlaskForm):    book = StringField('Book Name', validators=[DataRequired()])    submit = SubmitField('Submit')@app.route('/')def home():    with app.app_context():        result = db.session.execute(db.select(Book_Store).order_by(desc(Book_Store.rating)))        all_books = result.scalars()        return render_template('index.html', books=all_books)@app.route("/add", methods=["GET", "POST"])def add():    if request.method == "POST":        with app.app_context():            new_book = Book_Store(title=request.form["title"], author=request.form["author"], rating=request.form["rating"])            db.session.add(new_book)            db.session.commit()        return redirect(url_for('home'))    return render_template("add.html")@app.route("/delete/<int:id>", methods=["POST"])def delete(id):    if request.method == "POST":        with app.app_context():            book_to_delete = Book_Store.query.get(id)            if book_to_delete:                db.session.delete(book_to_delete)                db.session.commit()                return redirect(url_for("home"))            else:                return "Book not found"    else:        return "Method Not Allowed", 405@app.route("/edit", methods=["GET", "POST"])def edit():    if request.method == "POST":        book_id = request.form["id"]        book_to_update = Book_Store.query.get_or_404(book_id)        book_to_update.title = request.form["title"]        book_to_update.author = request.form["author"]        book_to_update.rating = request.form["rating"]        db.session.commit()        return redirect(url_for('home'))    else:        book_id = request.args.get('id')        book_selected = Book_Store.query.get_or_404(book_id)        return render_template("update.html", book=book_selected)if __name__ == "__main__":    app.run(debug=True)